mapper.select:
select 

from zipkin_spans
where trace_id_high = #{traceIdHigh,jdbcType=BIGINT}
  and trace_id = #{traceId,jdbcType=BIGINT}
  and id = #{id,jdbcType=BIGINT}

mapper.delete:
delete from zipkin_spans
where trace_id_high = #{traceIdHigh,jdbcType=BIGINT}
  and trace_id = #{traceId,jdbcType=BIGINT}
  and id = #{id,jdbcType=BIGINT}

mapper.update:
update zipkin_spans


    name = #{name,jdbcType=VARCHAR},


    remote_service_name = #{remoteServiceName,jdbcType=VARCHAR},


    parent_id = #{parentId,jdbcType=BIGINT},


    debug = #{debug,jdbcType=BIT},


    start_ts = #{startTs,jdbcType=BIGINT},


    duration = #{duration,jdbcType=BIGINT},


where trace_id_high = #{traceIdHigh,jdbcType=BIGINT}
  and trace_id = #{traceId,jdbcType=BIGINT}
  and id = #{id,jdbcType=BIGINT}

update zipkin_spans
set name = #{name,jdbcType=VARCHAR},
  remote_service_name = #{remoteServiceName,jdbcType=VARCHAR},
  parent_id = #{parentId,jdbcType=BIGINT},
  debug = #{debug,jdbcType=BIT},
  start_ts = #{startTs,jdbcType=BIGINT},
  duration = #{duration,jdbcType=BIGINT}
where trace_id_high = #{traceIdHigh,jdbcType=BIGINT}
  and trace_id = #{traceId,jdbcType=BIGINT}
  and id = #{id,jdbcType=BIGINT}
